mapper.select:
SELECT * FROM (
	SELECT rownum rnum, F.* FROM (
		SELECT
			fno, title, content , userid
			, hit, write_date
		FROM freeboard
		ORDER BY fno DESC
	) F
) freeboard
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
update freeboard
set hit= hit + 1
where fno =  #{fno}
